//EXAMPLE 3
const int[1,NCB] lockLimit = 2; //Max number of reservation (pairs)
const int[1,NSEG] resLimit = 2; //Max number of locks
const Seg none = {-1, {-1, -1}, 0}; 
const Seg s0 = {0, {0, 1}, 2}; 
const Seg s1 = {1, {1, 2}, 2};
const Seg s2 = {2, {2, 3}, 2};
const Seg s3 = {3, {2, 3}, 1};
const Seg s4 = {4, {3, 4}, 2};
const Seg segments[NSEG] = {s0, s1, s2, s3, s4};


const Seg routes[NT][NSEG]= {{s0,s1,s2,s4,none}, {s4,s3,s1,s0, none}};
const int length = 4; //route length
const int[0,1] directions[NT]= {0,1}; //0 = left to right, 1 = right to left
const int CBs[NCB][3]= {{0,-1,-1}, {0,1,-1}, {1,2,3}, {4,2,3}, {4,-1,-1}}; //(stem, plus, minus)
const int boxes[NCB] = { -1, -1, 0, 1, -1};

chan resSeg[NCB][NT][NSEG]; //[switch box][train id][segment id]
broadcast chan switchh[NCB];
chan reqLock[NCB][NT][NSEG][NSEG]; //lock sb for t between two segments
chan OK[NT];
chan notOK[NT];
chan pass[NCB][NT]; //t passing s connected to sb
chan OKsb[NCB];

template.declaration:
int i = 1;
Seg curSeg;
Seg nextSeg;
int nextSB;

int cb = 0;
int li = 1;
int ri = 1;

int reachedCBReservation(){
   if (!directions[id]){
      return routes[id][li].cbs[0];
    } else {
      return routes[id][li].cbs[1];
    }
}

int prevLockSeg(){
    return routes[id][li-1].id;
}

int nextLockSeg(){
    return routes[id][li].id;
}

int res[3] = {-1, -1, -1};
bool result;
int tid = -1;
int lockedBy = -1;
bool sw = false;
int stem;
int connected;

bool reserve(int tid, int sid){
  Seg s = segments[sid];

  for(i : int[0,2]) {
    if (CBs[id][i] == s.id && res[i] == -1) {
      res[i] = tid;
      return true;
    }
  }
  return false;
}

bool lock(int tid, int s1, int s2){
  if(lockedBy == -1 && (s1 != s2) && (s1 == CBs[id][0] || s2 == CBs[id][0])){
      int s = 0;
      for (i:int[0,2]){
        if(CBs[id][i] == s1){
           if(res[i] == tid) {
               s++;
           }
        }
        if(CBs[id][i] == s2 ){
            if(res[i] == tid){
               s++;
            }
        }
      }
      if (s==2){
        lockedBy = tid;
        return true;
      }
    }
    return false;
}


bool clear(int tid){
  if (lockedBy == tid){
      lockedBy = -1;

      for (i : int[0,2]) 
      {
        if(res[i] == tid){
            res[i] = -1;
        }
      }
      return true;
    }
    return false;
}

bool swDir(int s1, int s2){ //+ = true, - = false
    if(connected != s1 && connected != s2){
        return true;
    }
    return false;
}

void updateConnected(){
    if(connected == CBs[id][1]){
        connected := CBs[id][2];
    } else {
        connected := CBs[id][1];
    }
}
