nta.declaration:
const int NCB = 5; //Number of control boxes
const int NSEG = 5; //Number of segments
const int NT = 2; //Number of trains
const int NSWITCH = 2; //Number of switchable points

//TYPES
typedef int[0, NT-1] t_id;
typedef int[0, NCB-1]  CB_id;
typedef int[0, NSWITCH-1] SW_id;
typedef int[0, NSEG-1] Seg_id;

//EXAMPLE
const int[1,NCB] lockLimit = 2; //Max number of reservation (pairs)
const int[1,NSEG] resLimit = 2; //Max number of locks

const int controlBoxes[NT][NCB] = {{0,1,2,3,4},{4,3,2,1,0}};
const int routes[NT][NSEG]= {{0,1,2,4,-1}, {4,3,1,0, -1}};
const int CBs[NCB][3]= {{0,-1,-1}, {0,1,-1}, {1,2,3}, {4,2,3}, {4,-1,-1}}; //(stem, plus, minus)
const int switches[NCB] = { -1, -1, 0, 1, -1};

//Channels
chan resSeg[NCB][NT][NSEG]; //[control box][train id][segment id]
chan cancelSeg[NCB][NT][NSEG];  //[control box][train id][segment id]
chan switchh[NCB];
chan reqLock[NCB][NT][NSEG][NSEG]; //lock sb for t between two segments
chan OK[NT];
chan notOK[NT];
chan pass[NCB][NT]; //t passing s connected to sb
chan OKsb[NCB];

template.declaration:
int i = 1;
int curSeg;
int nextSeg;
int nextSB;

int cb = 0;
int ci = 1;
int ri = 1;
int cl = 1;

int route[NSEG];
int boxes[NCB];

void copyRouteFromServer() {
    for(i : int[0, NSEG-1]) {
        route[i] = routes[id][i];
    }
    curSeg = route[0];
    nextSeg = route[1];
}

void copyControlBoxInfo() {
    for(i : int[0, NCB-1]) {
        boxes[i] = controlBoxes[id][i];
    }
    nextSB = boxes[ri];
}

bool possibleToLockSegment() {
    return cl<NSEG && cl-i < lockLimit && route[cl] != -1;
}

bool possibleToReserveSegment() {
    return ri-i < resLimit && ri < NSEG && route[ri] != -1;
}

bool possibleToCancelReservation() {
    return ri > i && route[ri] != -1;
}

bool possibleToCrossBox() {
    return ri > i && cl > i;
}

bool hasTrainArrived() {
    return i>=NSEG || route[i] == -1;
}

int res[3] = {-1, -1, -1};
int[0,2] result;
int tid = -1;
int lockedBy = -1;
int stem;
int connected;

bool reserve(int tid, int sid){
  for(i : int[0,2]) {
    if (CBs[id][i] == sid && res[i] == -1) {
      res[i] = tid;
      return true;
    }
  }
  return false;
}

int[0,2] lock(int tid, int s1, int s2){
  if(lockedBy == -1 && (s1 != s2) && (s1 == CBs[id][0] || s2 == CBs[id][0])){
      int s = 0;
      for (i:int[0,2]){
        if(CBs[id][i] == s1){
           if(res[i] == tid) {
               s++;
           }
        }
        if(CBs[id][i] == s2 ){
            if(res[i] == tid){
               s++;
            }
        }
      }
      if (s==2  && connected != s1 &&  connected != s2){
        return 2;
      } else if (s==2) {
        return 1;
      }
    }
    return 0;
}


void clear(int tid){
     lockedBy = -1;

     for (i : int[0,2]) 
     {
       if(res[i] == tid){
           res[i] = -1;
       }
     }
}

void updateConnected(){
    if(connected == CBs[id][1]){
        connected := CBs[id][2];
    } else {
        connected := CBs[id][1];
    }
}
