nta.declaration:
const int NTRAIN = 2; //Number of trains
const int NCB = 5; //Number of control boxes
const int NPOINT = 2; //Number of points
const int NSEG = 5; //Number of segments


//TYPES
typedef int[0, NTRAIN-1] t_id; //Train IDs
typedef int[0, NCB-1]  cB_id; //Control box IDs
typedef int[0, NPOINT-1] p_id; //Point IDs
typedef int[0, NSEG-1] seg_id; //Segment IDs
typedef int[-1, NTRAIN-1] tV_id; //Train IDs with -1
typedef int[-1, NCB-1] cBV_id; //Control Box IDs with -1
typedef int[-1, NPOINT-1] pV_id; //Point IDs with -1
typedef int[-1, NSEG-1] segV_id; //Segment IDs with -1

typedef struct {
    cB_id cb;
    tV_id res[3];
} InitialReserves;

//EXAMPLE
const int[1,NCB] lockLimit = 1; //Max number of reservations (pairs)
const int[1,NSEG] resLimit = 2; //Max number of locks

const segV_id segRoutes[NTRAIN][NSEG]= {{0,1,2,4,-1}, {4,3,1,0, -1}};
const cBV_id boxRoutes[NTRAIN][NCB] = {{0,1,2,3,4},{4,3,2,1,0}};
const segV_id cBs[NCB][3]= {{0,-1,-1}, {0,1,-1}, {1,2,3}, {4,2,3}, {4,-1,-1}}; //(stem, plus, minus)
const pV_id points[NCB] = { -1, -1, 0, 1, -1};
const bool pointInPlus[NPOINT] = {true, true};
const InitialReserves reserves[NTRAIN] = {{1, {0,-1,-1}}, {3, {1,-1,-1}}};

//Channels
chan resSeg[NCB][NTRAIN][NSEG];
chan reqLock[NCB][NTRAIN][NSEG][NSEG];
chan OK[NTRAIN];
chan notOK[NTRAIN];
chan pass[NCB];
chan passed[NCB];
chan switchPoint[NPOINT];
chan OKp[NCB];
urgent broadcast chan start;

template.declaration:
seg_id nextSegIndex = 1;
segV_id curSeg;
segV_id nextSeg;
cBV_id nextCB;
seg_id routeLength;

int[0,1] resBit = 0;
cB_id resCBIndex = 1;
seg_id resSegIndex = 1;
cB_id lockIndex = 1;

segV_id segments[NSEG];
cBV_id boxes[NCB];

void copySegments() {
    for(i : seg_id) {
        segments[i] = segRoutes[id][i];
        if(segments[i]>-1) {
            routeLength++;
        }
    }
    curSeg = segments[0];
    nextSeg = segments[1];
}

void copyControlBoxes() {
    for(i : cB_id) {
        boxes[i] = boxRoutes[id][i];
    }
    nextCB = boxes[1];
}

bool possibleToLockSegment() {
    return lockIndex <  routeLength && lockIndex - nextSegIndex < lockLimit && resSegIndex > lockIndex;
}

bool possibleToReserveSegment() {
    return resSegIndex < routeLength &&  resSegIndex - nextSegIndex < resLimit;
}

bool possibleToPassBox() {
    return resSegIndex > nextSegIndex && lockIndex > nextSegIndex;
}

bool hasArrived() {
    return nextSegIndex == routeLength;
}

bool validRoute() {
    bool result = true;
    for(j : int [0, NSEG-2]){
        result = result && !(segments[j] == -1 && segments[j+1] > -1);
    }
    return result;
}

tV_id res[3] = {-1, -1, -1};
int[0,2] result;
tV_id tid = -1;
tV_id lockedBy = -1;
segV_id stem = -1;
segV_id plus = -1;
segV_id minus = -1;
segV_id connected =-1;
pV_id point = -1;

const int error = 0;
const int noSwitch = 1;
const int doSwitch = 2;

void setInfo() {
    stem = cBs[id][0];
    plus = cBs[id][1];
    minus = cBs[id][2];
    point = points[id];
    connected = plus;

    if(point != -1 && !pointInPlus[point]){
        connected = minus;
    }

    for(i : t_id) {
        if (reserves[i].cb == id) {
            res = reserves[i].res;
        }
    }
}

bool reserve(t_id tid, seg_id sid){
  for(i : int[0,2]) {
    if (cBs[id][i] == sid && res[i] == -1) {
      res[i] = tid;
      return true;
    }
  }
  return false;
}

int[0,2] lock(t_id tid, seg_id s1, seg_id s2){
  if(lockedBy == -1 && (s1 != s2) && (s1 == stem || s2 == stem)){
      int s = 0;

      if((s1 == stem && s2 != connected) || (s2 == stem && s1 != connected)){
        return doSwitch;
      } else if ((s1 == stem && s2 == connected) || (s2 == stem && s1 == connected)){
        return noSwitch;
      }
   }
   return error;
}


void clear(){
     lockedBy = -1;

     res[0] = -1;
     if(connected == plus) {
        res[1] = -1;
     } else {
        res[2] = -1;
     }
}

void updateConnected(){
    if(connected == plus){
        connected = minus;
    } else {
        connected = plus;
    }
}
